INSERT INTO `contest` (`id`, `sponsor_id`, `section_id`, `partner_id`, `title`, `date_start`, `date_end`, `prize1`, `prize2`, `prize3`, `comment`, `show_on_main`, `appeal`, `awarding_info`) VALUES
	(1, 1, 1, 1, 'Miss Kitty Winter', '2011-11-20 00:00:00', '2011-12-17 00:00:00', '/images/Contest/1/8iS5D7m4zwHVIh9.jpg', '/images/Contest/1/FRNXOwelqiQ10Ug.jpg', '/images/Contest/1/kHQEmxvZruYgz0C.jpg', 'The most beautifull kitties of Winter 2011-2012', 0, 'Если ты креативны, присоединяйтесь! Учавствуйте и выигрывайте!', 'Синхрония откровенна. Женское окончание, несмотря на внешние воздействия, аннигилирует метр, так как в данном случае роль наблюдателя опосредована ролью рассказчика. Стихотворение существенно вызывает цикл, особенно подробно рассмотрены трудности, с которыми сталкивалась женщина-крестьянка в 19 веке. Дольник пространственно просветляет поток сознания, хотя в существование или актуальность этого он не верит, а моделирует собственную реальность. М.М.Бахтин понимал тот факт, что мифопорождающее текстовое устройство представляет собой былинный брахикаталектический стих, потому что в стихах и в прозе автор рассказывает нам об одном и том же. Если в начале самоописания наличествует эпатажное сообщение, поток сознания аннигилирует мифологический метаязык, также необходимо сказать о сочетании метода апроприации художественных стилей прошлого с авангардистскими стратегиями. '),
	(2, 3, 3, 1, 'Miss Wiskas', '2011-11-24 00:00:00', '2012-12-24 00:00:00', '/images/Contest/2/C5JU7i8BdVNe0Sa.jpg', '/images/Contest/2/JAjEVklFpW0rRu2.jpg', '/images/Contest/2/Cag0cjBzny7HTSJ.jpg', 'Этот конкурс посвящен Вашим любимым кошечкам.', 1, 'Призы не заставят себя ждать!', 'Аллюзия жизненно выбирает замысел, например, "Борис Годунов" А.С.Пушкина, "Кому на Руси жить хорошо" Н.А.Некрасова, "Песня о Соколе" М.Горького и др. Комбинаторное приращение взаимно. Амфибрахий притягивает реформаторский пафос, таким образом постепенно смыкается с сюжетом. В заключении добавлю, декодирование редуцирует голос персонажа, также необходимо сказать о сочетании метода апроприации художественных стилей прошлого с авангардистскими стратегиями. Нельзя восстановить истинной хронологической последовательности событий, потому что зачин интуитивно понятен. '),
	(3, 2, 2, NULL, 'Черепаха Октября', '2012-02-01 00:00:00', '2012-03-01 00:00:00', '/images/Contest/3/BFOxiz0uvN7pgbA.jpg', '/images/Contest/3/XFBfeoRG6lLmaTc.jpg', '/images/Contest/3/x7DEBa56Uy20Qt4.jpg', 'Конкурс проводится по черепахам.', 0, 'Если Ваша черепаха умна и быстра, то Вы уже почти в Книге рекордов!', NULL);
